CREATE TABLE user(
        id BIGINT NOT NULL,
        login VARCHAR(50) NOT NULL,
        password_hash VARCHAR(65) NOT NULL
);
CREATE TABLE auth(
    id BIGINT NOT NULL,
    user_id BIGINT NOT NULL,
    user_role VARCHAR(50) NOT NULL
);

